import React, { useState } from 'react';
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";

const CARD_OPTIONS = {
  iconStyle: 'solid',
  style: {
    base: {
      iconColor: 'rgb(110, 223, 170)',
      color: '#000',
      fontWeight: 500,
      fontSize: '16px',
      fontSmoothing: 'antialiased',
      ':-webkit-autofill': {color: 'rgb(110, 223, 170)'},
      '::placeholder': {color: 'rgb(110, 223, 170)'},
    },
    invalid: {
      iconColor: 'rgb(226, 110, 110)',
      color: 'rgb(226, 110, 110)',
    },
  },
};

function CheckoutForm() {

  const stripe = useStripe();
  const elements = useElements();
  const [userInfo, setUserInfo] = useState({
    name: '',
    email: '',
    address: '',
    city: '',
    state: '',
    zip: ''
  })

  const handleChange = (event) => {
    setUserInfo({
      ...userInfo,
      [event.target.name]: event.target.value
    })
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
    const { error, paymentMethod } = await stripe.createPaymentMethod({
      type: "card",
      card: elements.getElement(CardElement)
    })

    if (!error) {
      console.log("Stripe token generated!", paymentMethod)
    } else {
      console.log(error.message)
    }
  }

  const { name, email, address, city, state, zip } = userInfo

  return (
    <form id="checkout-form" onSubmit={handleSubmit}>
      <div className="FormRow">
        <label htmlFor="name">Name</label>
        <input type="text" id="name" value={name} onChange={handleChange} placeholder="Doug Programmer" />
      </div>
      <div className="FormRow">
        <label htmlFor="email">Email</label>
        <input type="text" id="email" value={email} placeholder="doug@example.com" />
      </div>
      <div className="FormRow">
        <label htmlFor="address">Address</label>
        <input type="text" id="address" value={address} placeholder="185 Berry Street Suite 400" />
      </div>
      <div className="FormRow">
        <label htmlFor="city">City</label>
        <input type="text" id="city" value={city} placeholder="Bellingham" />
      </div>
      <div className="FormRow">
        <label htmlFor="state">State</label>
        <input type="text" id="state" value={state} placeholder="WA" />
      </div>
      <div className="FormRow">
        <label htmlFor="zip">Zip</label>
        <input type="text" id="zip" value={zip} placeholder="98226" />
      </div>
      <hr/>
      <div className="FormRow">
        <CardElement options={CARD_OPTIONS} />
      </div>
      <button className="pay-btn">Pay</button>
    </form>
  )
}

export default CheckoutForm
